import React from 'react'
import {Typography} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField'
import Grid from '@material-ui/core/Grid';
import { useState } from 'react';
import InputMask from 'react-input-mask';
import validator from 'validator';
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';

import PersistentDrawerLeft from '../Components/Drawer'
const useStyles = makeStyles((theme) => ({
    paper: {
      marginTop: theme.spacing(8),
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
    },
    avatar: {
      margin: theme.spacing(1),
      backgroundColor: theme.palette.secondary.main,
    },
    form: {
      width: '100%', // Fix IE 11 issue.
      marginTop: theme.spacing(1),
    },
    submit: {
      margin: theme.spacing(3, 0, 2),
    },
  }));
  


export default function RegisterMember(props){

    
    const [first_name,setfirst_name] = useState('');
    const [last_name,setlast_name] = useState(''); 
    const [Address,setAddress] = useState('');
    const [CNIC, setCNIC] = useState('');
    const [Phone_No,setPhone] = useState('');
    const [DateOfBirth,setDateOfBirth] = useState();
    const [username,setUser] = useState("");
    const [password,setPassword] = useState("");
    const [email,setEmail] = useState("");


    function alphanumeric(value){
        for (let c of value){
            if (isNaN(c)){
                break;        
            }
            return false;
        }

        for (let c of value){
            if (!/^[A-Za-z]$/.test(c)){
                break;        
            }
            return false;
        }

        return true
        

    }


    function checkpassstrength(value){
       
        if((value.length < 8 && !alphanumeric(value)) || value === "")
        {
            return(
                <Typography color="error">
                    Password must contain atleast 8 letters and must be alphanumeric       
                </Typography>
            )
        }
    }

    const emailtest = (value)=>{
        if(!validator.isEmail(value)){
            return (
                <Typography color="error">
                    Invalid Email       
                </Typography>
            )
        }
    }
    
    
    return(
        <div>
  
        <PersistentDrawerLeft message="Register Member">
        <Container component="main" maxWidth="md" > 
        <div className={useStyles.form}>
        
            <Grid container direction="row" md>
                <TextField required label="First Name" variant="outlined"  value={first_name} onChange={(event)=>{setfirst_name(event.target.value)}}/>
                <TextField required label="Last Name" variant="outlined"   value={last_name} onChange={(event)=>{setlast_name(event.target.value)}}/> 
            </Grid>

            <Grid>
                <InputMask mask="99999-9999999-9" maskChar=" " value={CNIC} onChange={(event)=>{setCNIC(event.target.value.replaceAll("-",""))}} >
                    {()=><TextField required label="CNIC" variant="outlined" />}
                </InputMask>
            </Grid>

            <Grid>
                <InputMask mask="9999-9999999" maskChar=" " value={Phone_No} onChange={(event)=>{setPhone(event.target.value.replace("-",""))}} >
                    {()=><TextField required label="Mobile Number" variant="outlined" />}
                </InputMask>
            </Grid>
            <Grid>
                <TextField required label="Address" multiline rows={4} value={Address} onChange={(event)=>{setAddress(event.target.value)}}/>
            </Grid>
        
            <Grid>
                <TextField  label="Date of Birth" type="date" defaultValue="2000-01-01" value={DateOfBirth} onChange={(event)=>{setDateOfBirth(event.target.value)}}/>
            </Grid>
            
            <Grid>
                <TextField margin="normal" required value={email} onChange={(event)=>{setEmail(event.target.value)}} label="E Mail" variant="outlined" />
                {emailtest(email)}
            </Grid>

            <Grid>
                <TextField margin="normal" required value={username} onChange={(event)=>{setUser(event.target.value)}} label="User ID" variant="outlined" />
            </Grid>
            
            <Grid>
                <TextField margin="normal" required value={password} onChange={(event)=>{setPassword(event.target.value)}}  label="Password" variant="outlined" type="password" />
                {checkpassstrength(password)}
            </Grid>

            <Button
                fullWidth
                variant="contained"
                color="primary"
                className={useStyles.submit}
                //onClick={(event)=>{HandleLogin()}}
            >
                Submit 
            </Button>
            
                
        </div>
        </Container>
        </PersistentDrawerLeft>
        </div>  
    )

};
